import { useState, useEffect } from 'react';
import './App.css';


function App() {

  const [depremler, setDepremler] = useState([]);
  const [durum, setDurum] = useState([]);

  useEffect(() => {
     if(depremler === null || depremler === undefined){
      fetch('https://api.orhanaydogdu.com.tr/deprem/live.php?limit=100')
      .then(response => response.json())
      .then(depremlerData => {
        setDepremler(depremlerData.result);
        setDurum(depremlerData.status);
      });
      }
      else if(depremler.length === 0){
        fetch('https://api.orhanaydogdu.com.tr/deprem/live.php?limit=100')
        .then(response => response.json())
        .then(depremlerData => {
          setDepremler(depremlerData.result);
          setDurum(depremlerData.status);
        });
      }
    const id = setInterval(depremler, 60000);
    return () => clearInterval(id);
  }, [depremler]);



  console.log(depremler)
  console.log(durum)
  return (
    <div>
      <h1>Depremler</h1>
      {depremler.map(deprem => (
        <div key={deprem.id}>
          <h2>Konum: {deprem.title}</h2>
          <p>Tarih: {deprem.date}</p>
          <p>Revize: {deprem.rev}</p>
          <p>Büyüklük: {deprem.mag}</p>
          <p>Derinlik: {deprem.depth + " km"}</p>
          <p>Enlem: {deprem.lat}</p>
          <p>Boylam: {deprem.lng}</p>
          </div>
      ))}

    </div>
  );
}

export default App;
