import { useState, useEffect } from 'react';
import './App.css';
import XMLParser from 'react-xml-parser';


function App() {

  const [depremler, setDepremler] = useState([]);
  const [durum, setDurum] = useState([]);

  useEffect(() => {
      fetch('http://www.koeri.boun.edu.tr/scripts/lst4.asp',{
        mode: 'no-cors'
      })
      .then(response => response.json())
      .then(depremlerData => {
        var xml = new XMLParser().parseFromString(depremlerData);
        setDepremler(depremlerData.result);
        setDurum(depremlerData.status);
      });

  }, [depremler]);



  console.log(depremler)
  console.log(durum)
  return (
    <div>
      <h1>Depremler</h1>
      {depremler.map(deprem => (
        <div key={deprem.id}>
          <h2>Konum: {deprem.title}</h2>
          <p>Tarih: {deprem.date}</p>
          <p>Revize: {deprem.rev}</p>
          <p>Büyüklük: {deprem.mag}</p>
          <p>Derinlik: {deprem.depth + " km"}</p>
          <p>Enlem: {deprem.lat}</p>
          <p>Boylam: {deprem.lng}</p>
          </div>
      ))}

    </div>
  );
}

export default App;
