import toplanmaData from "../api/toplanma.json"
import { useEffect, useState } from "react";



function Yardim() {
    
    const [toplanma, setToplanma] = useState([]);

    useEffect(() => {
        setToplanma(toplanmaData);
    }, [toplanma]);

  
    
    return ( 
    
        <div className='container'>
      <h1>Bakanlığın Belirlediği Toplanma Alanları</h1>
      
      <table className='deprem'>
        <tr className='inTable'>
            <th>Şehir</th>
            <th>Alan</th>
            </tr>
      {toplanma.map(top => (
            <tr key={key}>
            <td>{top.şehir}</td>
            <td><a href={top.konum}>{top.alan}</a></td>
            </tr>
      ))}
      </table>
      
      
    </div>

     );
}

export default Yardim;
