import { useState, useEffect } from 'react';
import './App.css';


function App() {

  const [depremler, setDepremler] = useState([]);
  const [durum, setDurum] = useState([]);

  useEffect(() => {
     if(depremler === null || depremler.length === 0 || depremler === undefined){
      fetch('https://api.orhanaydogdu.com.tr/deprem/live.php?limit=100')
      .then(response => response.json())
      .then(depremlerData => {
        setDepremler(depremlerData.result);
        setDurum(depremlerData.status);
      });
      }
  }, [depremler]);



  console.log(depremler)
  console.log(durum)
  return (
    <div>
      <h1>Deprem</h1>
      { depremler !== null && depremler.length < 0 && depremler != undefined ? ( 
        <div>
          <h2>Deprem yok</h2>
        </div>
      ) : (
        <div>
          <h2>Deprem var</h2>
        </div>
      )
      }
    </div>
  );
}

export default App;
