import { useState, useEffect } from 'react';
import './App.css';


function App() {

  const [depremler, setDepremler] = useState([]);
  const [durum, setDurum] = useState([]);

  useEffect(() => {
     if(depremler === null || depremler === undefined){
      fetch('https://api.orhanaydogdu.com.tr/deprem/live.php?limit=100')
      .then(response => response.json())
      .then(depremlerData => {
        setDepremler(depremlerData.result);
        setDurum(depremlerData.status);
      });
      }
      else if(depremler.length === 0){
        fetch('https://api.orhanaydogdu.com.tr/deprem/live.php?limit=100')
        .then(response => response.json())
        .then(depremlerData => {
          setDepremler(depremlerData.result);
          setDurum(depremlerData.status);
        });
      }
  }, [depremler]);



  console.log(depremler)
  console.log(durum)
  return (
    <div>
      <h1>Depremler</h1>
      {depremler.map (deprem => (
        <div className="deprem">
          <h2>{deprem.title}</h2>
          <p>{deprem.date}</p>
          <p>{deprem.time}</p>
          <p>{deprem.location}</p>
          <p>{deprem.magnitude}</p>
          <p>{deprem.depth}</p>
          <p>{deprem.latitude}</p>
          <p>{deprem.longitude}</p>
        </div>
          ))}
    </div>
  );
}

export default App;
